import { useFormik } from "formik";
import { FunctionComponent, useContext, useEffect, useState } from "react";
import { Modal } from "react-bootstrap";
import * as yup from "yup";
import { SiteTheme } from "../App";
import Card from "../interfaces/card";
import { getCardByDes, updateCardByDes } from "../services/cardList";


interface UpdateCardModalProps {
    show: boolean;
    onHide: Function;
    cardDes: string;
    change: boolean;
    setChange: Function;
}

const UpdateCardModal: FunctionComponent<UpdateCardModalProps> = ({ show, onHide, cardDes }) => {
    let [cardInfo, setCardInfo] = useState<Card>();
    useEffect(() => {
        console.log(cardDes);
        getCardByDes(JSON.parse(localStorage.getItem("userId") as string), cardDes).then((res) => { setCardInfo(res as Card) }).catch((err) => console.log(err));
    }, [])
    let formik = useFormik({
        initialValues: { businessName: cardInfo?.businessName as string, businessDescription: "", businessAddress: "", businessPhone: "", businessImage: "" },
        validationSchema: yup.object({
            businessName: yup.string().required().min(3),
            businessDescription: yup.string().required().min(10),
            businessAddress: yup.string().required().min(10),
            businessPhone: yup.string().required().min(9),
            businessImage: yup.string().required()
        }),
        onSubmit: (values: Card, { resetForm }) => {
            // updateCardByDes(JSON.parse(localStorage.getItem("userId") as string), values, cardDes)
            // resetForm();

        }
    })

    const theme = useContext(SiteTheme);
    return <>
        <Modal
            show={show}
            onHide={() => onHide()}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
        >
            <Modal.Header style={{ color: theme.color, backgroundColor: theme.background }} closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                </Modal.Title>
            </Modal.Header>

            <Modal.Body style={{ color: theme.color, backgroundColor: theme.background }}>


            </Modal.Body>

            <Modal.Footer style={{ color: theme.color, backgroundColor: theme.background }}>
                <button onClick={() => onHide()} className="btn btn-info"> cancel </button>
            </Modal.Footer>
        </Modal>
    </>
}

export default UpdateCardModal;
