import { useFormik } from "formik";
import { FunctionComponent, useContext, useState } from "react";
import * as yup from "yup";
import { SiteTheme } from "../App";
import Card from "../interfaces/card";
import { addCardToList, getAllCards } from "../services/cardList";
import Footer from "./footer";

interface AddCardProps{

}

const AddCard: FunctionComponent<AddCardProps> = () => {
    let cArr: Card[] = [];
    let [arr,setArr] = useState<Card[]>([])
    let formik = useFormik({
        initialValues:{businessName: "", businessDescription: "", businessAddress: "", businessPhone: "", businessImage:""},
        validationSchema: yup.object({
            businessName: yup.string().required().min(3),
            businessDescription: yup.string().required().min(10),
            businessAddress: yup.string().required().min(10),
            businessPhone: yup.string().required().min(9),
            businessImage: yup.string().required()
        }),
        onSubmit: (values: Card) => {
            let userId = JSON.parse(sessionStorage.getItem("userId") as string);
            addCardToList(userId,values).then(() => {console.log("success!")}).catch((err) => console.log(err));
            getAllCards().then((res:any) => {
                setArr(res);
            }).catch((err) => console.log(`${err}`))
            console.log(arr);

        }
    })
    const theme = useContext(SiteTheme);
    return <>
    <div style={{color: theme.color, background: theme.background}} className="darkM">
        <div style={{ marginRight:"0"}}  className="row">
            <div style={{padding: "50px",paddingBottom:"90px"}} className="container col-md-4">
                <h1 className="display-4 text-center" >Add Card</h1>
                <form onSubmit={formik.handleSubmit}> 
                <div className="form-floating mb-3">
                <input
                    type="text"
                    className="form-control"
                    id="floatingInput"
                    placeholder="example@test.com"
                    name="businessName"
                    value={formik.values.businessName}
                    onChange={formik.handleChange}
                    onBlur={formik.handleBlur}
                />
                <label style={{color: "#161616"}}  htmlFor="floatingInput">Name</label>
                {formik.touched.businessName && formik.errors.businessName && (
                    <small className="text-danger">{formik.errors.businessName}</small>
                )}
                </div> 
                <div className="form-floating mb-3">
                    <input
                    type="text"
                    className="form-control"
                    id="floatingInput"
                    placeholder="example@test.com"
                    name="businessDescription"
                    value={formik.values.businessDescription}
                    onChange={formik.handleChange}
                    onBlur={formik.handleBlur}
                    />
                    <label style={{color: "#161616"}} htmlFor="floatingInput">Description</label>
                    {formik.touched.businessDescription && formik.errors.businessDescription && (
                    <small className="text-danger">{formik.errors.businessDescription}</small>
                    )}
                </div>
                <div className="form-floating mb-3">
                    <input
                    type="text"
                    className="form-control"
                    id="floatingInput"
                    placeholder="example@test.com"
                    name="businessAddress"
                    value={formik.values.businessAddress}
                    onChange={formik.handleChange}
                    onBlur={formik.handleBlur}
                    />
                    <label style={{color: "#161616"}} htmlFor="floatingInput">Address</label>
                    {formik.touched.businessAddress && formik.errors.businessAddress && (
                    <small className="text-danger">{formik.errors.businessAddress}</small>
                    )}
                </div>
                <div className="form-floating mb-3">
                    <input
                    type="text"
                    className="form-control"
                    id="floatingInput"
                    placeholder="example@test.com"
                    name="businessPhone"
                    value={formik.values.businessPhone}
                    onChange={formik.handleChange}
                    onBlur={formik.handleBlur}
                    />
                    <label style={{color: "#161616"}} htmlFor="floatingInput">Phone</label>
                    {formik.touched.businessPhone && formik.errors.businessPhone && (
                    <small className="text-danger">{formik.errors.businessPhone}</small>
                    )}
                </div>
                <div className="form-floating mb-3">
                    <input
                    type="text"
                    className="form-control"
                    id="floatingInput"
                    placeholder="example@test.com"
                    name="businessImage"
                    value={formik.values.businessImage}
                    onChange={formik.handleChange}
                    onBlur={formik.handleBlur}
                    />
                    <label style={{color: "#161616"}} htmlFor="floatingInput">Image</label>
                    {formik.touched.businessImage && formik.errors.businessImage && (
                    <small className="text-danger">{formik.errors.businessImage}</small>
                    )}
                </div>
                <button style={{color: theme.color, background: theme.btn}} type="submit" className="btn w-100" disabled={!formik.dirty || !formik.errors}>Add Card</button>
                </form>
            </div>

            <div style={{padding:"0"}} id="pic" className="col-md-7">
                        <img style={{height: "100%"}} className="w-100" src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTy4k7clv9pIuHkXhUqHbtvLeAOUqB-VpznfZzWRWJnvQeCbSgcJ2TGyfJggJhqo3LSol0&usqp=CAU" alt="" />
            </div>
            <Footer />
        </div>
    </div>
    </>
}

export default AddCard;
