import { FunctionComponent, useContext, useEffect, useState } from "react";
import { SiteTheme } from "../App";
import Card from "../interfaces/card";
import { getAllCards } from "../services/cardList";

interface AllCardsProps {

}

const AllCards: FunctionComponent<AllCardsProps> = () => {
    let [allCardsArr, setAllCardsArr] = useState<Card[]>([]);

    useEffect(() => {
        getAllCards().then((res) => setAllCardsArr(res as Card[]));
    }, [])
    const theme = useContext(SiteTheme);
    return <>
        {allCardsArr.length ?
            (
                <>
                    {allCardsArr.map((card) => (
                        <div key={card.id} className="darkM card" style={{ display: "inline-block", padding: "10px", margin: "5px", width: "20rem", border: `5px solid ${theme.color}`, backgroundColor: theme.background, color: theme.color }}>
                            <h5 className="cart-title text-center mt-2" >{card.businessName}</h5>
                            <hr />
                            <img style={{ height: "100%", borderRadius: "6px" }} src={card.businessImage} className="align-self-center card-img-top" alt="..." />
                            <div className="card-body">
                                <h5 className="card-text mt-1">{card.businessPhone}</h5>
                                <p className="card-text mt-3">{card.businessDescription}</p>
                                <div className="text-center mt-5">
                                    <button className="btn btn-danger me-2"><i className="fa-solid fa-trash-can"></i></button>
                                    <button className="btn btn-warning"><i className="fa-solid fa-pen-to-square"></i></button>
                                </div>
                            </div>
                        </div>
                    ))}
                </>
            )
            :
            (
                <h1>No cards in data base</h1>
            )}
    </>
}

export default AllCards;
