import { FunctionComponent, useContext, useEffect, useState } from "react";
import Card from "../interfaces/card";
import * as yup from "yup";
import { useFormik } from "formik";
import { SiteTheme } from "../App";
import { getCardByDes } from "../services/cardList";

interface UpdateCardProps {
    onHide: Function;
    cardDes: string;
    change: boolean;
    setChange: Function;


}

const UpdateCard: FunctionComponent<UpdateCardProps> = ({ onHide, cardDes, change, setChange }) => {
    let [cardInfo, setCardInfo] = useState<Card>({
        businessName: "",
        businessDescription: cardDes,
        businessAddress: "",
        businessPhone: "",
        businessImage: ""
    });
    useEffect(() => {
        getCardByDes(JSON.parse(localStorage.getItem("userId") as string), cardDes).then((res) => { setCardInfo(res as Card); console.log(cardInfo) }).catch((err) => console.log(err));
    }, [])
    const theme = useContext(SiteTheme);


    let formik = useFormik({
        initialValues: { businessName: cardInfo.businessName, businessDescription: cardInfo.businessDescription, businessAddress: cardInfo.businessAddress, businessPhone: cardInfo.businessPhone, businessImage: cardInfo.businessImage },
        validationSchema: yup.object({
            businessName: yup.string().required().min(3),
            businessDescription: yup.string().required().min(10),
            businessAddress: yup.string().required().min(10),
            businessPhone: yup.string().required().min(9),
            businessImage: yup.string().required()
        }),
        onSubmit: (values: Card, { resetForm }) => {
            // updateCardByDes(JSON.parse(localStorage.getItem("userId") as string), values, cardDes)
            // resetForm();

        }
    })

    return <>
        <div style={{ padding: "50px", paddingBottom: "90px" }} className="container col-md-6">
            <h1 className="display-4 text-center" >Add Card</h1>
            <form onSubmit={formik.handleSubmit}>
                <div className="form-floating mb-3">
                    <input
                        type="text"
                        className="form-control"
                        id="floatingInput"
                        placeholder="example@test.com"
                        name="businessName"
                        value={formik.values.businessName}
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                    />
                    <label style={{ color: "#161616" }} htmlFor="floatingInput">Name</label>
                    {formik.touched.businessName && formik.errors.businessName && (
                        <small className="text-danger">{formik.errors.businessName}</small>
                    )}
                </div>
                <div className="form-floating mb-3">
                    <input
                        type="text"
                        className="form-control"
                        id="floatingInput"
                        placeholder="example@test.com"
                        name="businessDescription"
                        value={formik.values.businessDescription}
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                    />
                    <label style={{ color: "#161616" }} htmlFor="floatingInput">Description</label>
                    {formik.touched.businessDescription && formik.errors.businessDescription && (
                        <small className="text-danger">{formik.errors.businessDescription}</small>
                    )}
                </div>
                <div className="form-floating mb-3">
                    <input
                        type="text"
                        className="form-control"
                        id="floatingInput"
                        placeholder="example@test.com"
                        name="businessAddress"
                        value={formik.values.businessAddress}
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                    />
                    <label style={{ color: "#161616" }} htmlFor="floatingInput">Address</label>
                    {formik.touched.businessAddress && formik.errors.businessAddress && (
                        <small className="text-danger">{formik.errors.businessAddress}</small>
                    )}
                </div>
                <div className="form-floating mb-3">
                    <input
                        type="text"
                        className="form-control"
                        id="floatingInput"
                        placeholder="example@test.com"
                        name="businessPhone"
                        value={formik.values.businessPhone}
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                    />
                    <label style={{ color: "#161616" }} htmlFor="floatingInput">Phone</label>
                    {formik.touched.businessPhone && formik.errors.businessPhone && (
                        <small className="text-danger">{formik.errors.businessPhone}</small>
                    )}
                </div>
                <div className="form-floating mb-3">
                    <input
                        type="text"
                        className="form-control"
                        id="floatingInput"
                        placeholder="example@test.com"
                        name="businessImage"
                        value={formik.values.businessImage}
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                    />
                    <label style={{ color: "#161616" }} htmlFor="floatingInput">Image</label>
                    {formik.touched.businessImage && formik.errors.businessImage && (
                        <small className="text-danger">{formik.errors.businessImage}</small>
                    )}
                </div>
                <button style={{ color: theme.color, background: theme.btn }} type="submit" className="btn w-100" disabled={!formik.dirty || !formik.isValid}>Add Card</button>
            </form>
        </div>
    </>
}

export default UpdateCard;
