import { useFormik } from "formik";
import { FunctionComponent, useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import * as yup from "yup";
import { SiteTheme } from "../App";
import User from "../interfaces/user";
import { addCardList } from "../services/cardList";
import { errorMsg, successMsg } from "../services/feedbacks";
import { checkUser } from "../services/userService";
import Footer from "./footer";


interface SignInProps{
    setLogged: Function
    setBis: Function
}

const SignIn: FunctionComponent<SignInProps> = ({setLogged,setBis}) => {
    let nav = useNavigate();
    let formik = useFormik({
        initialValues:{email:"",password:""},
        validationSchema: yup.object({
            email: yup.string().required().email(),
            password: yup.string().required().min(8)
        }),
        onSubmit: (values: User) =>{
            checkUser(values)
            .then((res) =>{
                if(res.data.length){
                    if(res.data[0].isBusiness){
                        addCardList(res.data[0].id).then(() => console.log("cardlist added")).catch((err) => console.log(err));
                    }
                    successMsg("Logged in succesfuly!");
                    sessionStorage.setItem("isLogged",JSON.stringify({'logged': 'true', 'isBis': `${res.data[0].isBusiness}`}));
                    sessionStorage.setItem("userId",`${res.data[0].id}`)
                    setLogged(true);
                    setBis(res.data[0].isBusiness)
                    nav("/");

                }else{
                    errorMsg("Invalid Email Or Password");
                }
            })
            .catch((err) => console.log(err));
        }
    })
    const theme = useContext(SiteTheme);
    return <>
        <div style={{color: theme.color, background: theme.background}} className="darkM">
        <div style={{height:"83.5vh", marginRight:"0"}}  className="row">
            <div style={{padding: "50px"}} className="container col-md-4 mt-5">
                <h1 className="display-4 text-center" >SIGN IN</h1>
                <form onSubmit={formik.handleSubmit}> 
                <div className="form-floating mb-3">
                <input
                    type="email"
                    className="form-control"
                    id="floatingInput"
                    placeholder="example@test.com"
                    name="email"
                    value={formik.values.email}
                    onChange={formik.handleChange}
                    onBlur={formik.handleBlur}
                />
                <label style={{color: "#161616"}}  htmlFor="floatingInput">Email</label>
                {formik.touched.email && formik.errors.email && (
                    <small className="text-danger">{formik.errors.email}</small>
                )}
                </div> 
                <div className="form-floating mb-3">
                    <input
                    type="password"
                    className="form-control"
                    id="floatingInput"
                    placeholder="example@test.com"
                    name="password"
                    value={formik.values.password}
                    onChange={formik.handleChange}
                    onBlur={formik.handleBlur}
                    />
                    <label style={{color: "#161616"}} htmlFor="floatingInput">password</label>
                    {formik.touched.password && formik.errors.password && (
                    <small className="text-danger">{formik.errors.password}</small>
                    )}
                </div>
                <div className="text-center mb-3">
                    <Link to="/signup">New user? Click here!</Link>
                </div>
                <button style={{color: theme.color, background: theme.btn}} type="submit" className="btn w-100" disabled={!formik.dirty || !formik.errors}>Sign in</button>
                </form>
            </div>

            <div style={{padding:"0"}} id="pic" className="col-md-7">
                        <img style={{height: "85vh"}} className="w-100" src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTy4k7clv9pIuHkXhUqHbtvLeAOUqB-VpznfZzWRWJnvQeCbSgcJ2TGyfJggJhqo3LSol0&usqp=CAU" alt="" />
            </div>

        </div>
    </div>
    <Footer />
    </>
}

export default SignIn;
