import { FunctionComponent, useContext } from "react";
import { Modal } from "react-bootstrap";
import { SiteTheme } from "../App";
import Card from "../interfaces/card";

interface ColorModalProps{
    show: boolean;
    onHide: Function;
    cardInfo: Card;
    setChange: Function;
    change: boolean
}

const ColorModal: FunctionComponent<ColorModalProps> = ({show,onHide,cardInfo,change,setChange}) => {
    const theme = useContext(SiteTheme);
    return <>
    <Modal
        show={show}
        onHide={() => onHide()}
        // size="sm"
        aria-labelledby="contained-modal-title-vcenter"
        centered
    >

      <Modal.Body style={{ color: theme.color, backgroundColor: theme.background, borderRadius:"10px" }}>
        <div id="colorBtns" style={{backgroundColor: theme.background, color: theme.color}}>
            <button style={{margin:"15px", width:"40px", height:"40px", backgroundColor:"#F4FF00"}} className="btn"></button>
            <button style={{margin:"15px", width:"40px", height:"40px", backgroundColor:"#002EFF"}} className="btn"></button>
            <button style={{margin:"15px", width:"40px", height:"40px", backgroundColor:"#00FF23"}} className="btn"></button>
            <button style={{margin:"15px", width:"40px", height:"40px", backgroundColor:"#F82D31"}} className="btn"></button>
            <button style={{margin:"15px", width:"40px", height:"40px", backgroundColor:"#A200F4"}} className="btn"></button>
            <button style={{margin:"15px", width:"40px", height:"40px", backgroundColor:"black"}} className="btn"></button>
            <button style={{margin:"15px", width:"40px", height:"40px", backgroundColor:"black"}} className="btn"></button>
            <button style={{margin:"15px", width:"40px", height:"40px", backgroundColor:"black"}} className="btn"></button>
            <button style={{margin:"15px", width:"40px", height:"40px", backgroundColor:"black"}} className="btn"></button>
            <button style={{margin:"15px", width:"40px", height:"40px", backgroundColor:"black"}} className="btn"></button>
            <button style={{margin:"15px", width:"40px", height:"40px", backgroundColor:"black"}} className="btn"></button>
            <button style={{margin:"15px", width:"40px", height:"40px", backgroundColor:"black"}} className="btn"></button>
        </div>
       </Modal.Body>


    </Modal>
    </>
}

export default ColorModal;
