import { FunctionComponent, useContext } from "react";
import { Modal } from "react-bootstrap";
import { SiteTheme } from "../App";
import Card from "../interfaces/card";

interface CardModalProps{
    show: boolean;
    onHide: Function;
    cardInfo: Card;
}

const CardModal: FunctionComponent<CardModalProps> = ({show,onHide,cardInfo}) => {
    const theme = useContext(SiteTheme);
    return <>
            <Modal
            show={show}
            onHide={() => onHide()}
            aria-labelledby="contained-modal-title-vcenter"
            centered
        >

            <div id="showCard" className="card col-12 container text-center" style={{ border: `5px solid ${theme.color}`, backgroundColor: theme.background, color: theme.color }}>
                <h3 className="cart-title text-center mt-4" >{cardInfo.businessName}</h3>
                <hr />
                <div style={{height:"40vh"}}>
                    <img style={{ height: "100%", borderRadius: "6px" }} src={cardInfo.businessImage} className="align-self-center card-img-top" alt="..." />
                </div>
                <div className="card-body">
                    <p className="card-text mt-3">{cardInfo.businessDescription}</p>
                    <p className="card-text mt-1">{cardInfo.businessPhone}</p>
                    <p className="card-text mt-2" >{cardInfo.businessAddress}</p>
                </div>
            </div>
        </Modal>
    </>
}

export default CardModal;
