import { FunctionComponent, useEffect, useState } from "react";
import Card from "../interfaces/card";
import { getCardListById } from "../services/cardList";

interface MyCardsProps {

}

const MyCards: FunctionComponent<MyCardsProps> = () => {
    let [listCards, setListCards] = useState<Card[]>([]);
    useEffect(() => {
        getCardListById(JSON.parse(localStorage.getItem("userId") as string)).then((res) => { setListCards(res.data.cards) }).catch((err) => console.log(err));
    }, [])
    const theme = useContext(SiteTheme);
    return <>
        <div className="container" style={{ height: "86vh", overflow: "auto" }}>
            {listCards.length ?
                (
                    <div className="row mt-5">
                        {listCards.map((card) => (
                            <div key={card.id} className="card me-2" style={{ width: "20rem", padding: "10px" }}>
                                <h5 className="cart-title text-center mt-2" >{card.businessName}</h5>
                                <hr />
                                <img style={{ height: "100%", borderRadius: "6px" }} src={card.businessImage} className="align-self-center card-img-top" alt="..." />
                                <div className="card-body">
                                    <h5 className="card-text mt-1">{card.businessPhone}</h5>
                                    <p className="card-text mt-3">{card.businessDescription}</p>
                                    <div className="text-center mt-5">
                                        <button className="btn btn-danger me-2"><i className="fa-solid fa-trash-can"></i></button>
                                        <button className="btn btn-warning"><i className="fa-solid fa-pen-to-square"></i></button>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                )
                :
                (
                    <h1 className="text-center mt-4">You have no cards!</h1>
                )}


        </div>
    </>
}

export default MyCards;
