import { FunctionComponent, useContext, useEffect, useState } from "react";
import { SiteTheme } from "../App";
import Card from "../interfaces/card";
import { getCardListById } from "../services/cardList";
import DeleteCardModal from "./deleteCardModal";
import UpdateCardModal from "./updateCardModal";

interface MyCardsProps {

}

const MyCards: FunctionComponent<MyCardsProps> = () => {
    let [listCards, setListCards] = useState<Card[]>([]);
    let [openDeleteModal, setOpenDeleteModal] = useState<boolean>(false);
    let [openUpdateModal, setOpenUpdateModal] = useState<boolean>(false);
    let [cardPhone, setCardPhone] = useState<string>("");
    useEffect(() => {
        getCardListById(JSON.parse(localStorage.getItem("userId") as string)).then((res) => { setListCards(res.data.cards) }).catch((err) => console.log(err));
    }, [])
    const theme = useContext(SiteTheme);
    return <>
        <div className="darkM" style={{ backgroundColor: theme.background, color: theme.color }}>
            <div className="darkM container" style={{ height: "86vh", overflow: "auto", backgroundColor: theme.background, color: theme.color }}>
                {listCards.length ?
                    (
                        <div className=" mt-5 text-center">
                            {listCards.map((card) => (
                                <div key={card.id} className="darkM card" style={{ display: "inline-block", padding: "10px", margin: "5px", width: "20rem", border: `5px solid ${theme.color}`, backgroundColor: theme.background, color: theme.color }}>
                                    <h5 className="cart-title text-center mt-2" >{card.businessName}</h5>
                                    <hr />
                                    <img style={{ height: "100%", borderRadius: "6px" }} src={card.businessImage} className="align-self-center card-img-top" alt="..." />
                                    <div className="card-body">
                                        <h5 className="card-text mt-1">{card.businessPhone}</h5>
                                        <p className="card-text mt-3">{card.businessDescription}</p>
                                        <div className="text-center mt-5">
                                            <button onClick={() => { setOpenDeleteModal(true); setCardPhone(card.businessPhone) }} className="btn btn-danger me-2"><i className="fa-solid fa-trash-can"></i></button>
                                            <button onClick={() => { setOpenUpdateModal(true); setCardPhone(card.businessPhone) }} className="btn btn-warning"><i className="fa-solid fa-pen-to-square"></i></button>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )
                    :
                    (
                        <h1 style={{ backgroundColor: theme.background, color: theme.color }} className="text-center mt-4">You have no cards!</h1>
                    )}
            </div>
        </div>
        <UpdateCardModal show={openUpdateModal} onHide={() => setOpenUpdateModal(false)} cardPhone={cardPhone} />
        <DeleteCardModal show={openDeleteModal} onHide={() => setOpenDeleteModal(false)} cardPhone={cardPhone} />
    </>
}

export default MyCards;
