import { FunctionComponent, useContext } from "react";
import { Modal } from "react-bootstrap";
import { SiteTheme } from "../App";
import Card from "../interfaces/card";

interface CardModalProps{
    show: boolean;
    onHide: Function;
    cardInfo: Card;
}

const CardModal: FunctionComponent<CardModalProps> = ({show,onHide,cardInfo}) => {
    const theme = useContext(SiteTheme);
    return <>
    <div>

    <Modal
            show={show}
            onHide={() => onHide()}
            aria-labelledby="contained-modal-title-vcenter"
            style={{width:"40vw"}}
            centered

        >
            {/* <Modal.Header  style={{ color: "red", backgroundColor: theme.background }} closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                </Modal.Title>
            </Modal.Header> */}
            <div className="card col-10 container text-center" style={{ width:"", border: `5px solid ${theme.color}`, backgroundColor: theme.background, color: theme.color }}>
                <h5 className="cart-title text-center mt-2" >{cardInfo.businessName}</h5>
                <hr />
                <div style={{height:"40vh"}}>
                    <img style={{ height: "100%", borderRadius: "6px" }} src={cardInfo.businessImage} className="align-self-center card-img-top" alt="..." />
                </div>
                <div className="card-body">
                    <p className="card-text mt-3">{cardInfo.businessDescription}</p>
                    <h5 className="card-text mt-1">{cardInfo.businessPhone}</h5>
                    <h5 className="card-text mt-2" >{cardInfo.businessAddress}</h5>
                </div>
            </div>
        </Modal>

    </div>

    </>
}

export default CardModal;
